import React, { useState } from "react";
import axios from "axios";
import MoodList from "./MoodList";

const MoodForm = () => {
  const [message, setMessage] = useState("");
  const [date, setDate] = useState(new Date().toISOString().slice(0, 10)); // Default to today's date
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setSuccess("");
    setError("");

    try {
      const response = await axios.post("http://localhost:5000/moods", {
        message,
        date,
      });
      setSuccess("Mood successfully submitted!");
      setMessage("");
      setDate(new Date().toISOString().slice(0, 10));
    } catch (err) {
      setError("Failed to submit your mood. Please try again.");
      console.error("Error:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Submit Your Mood</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="message">How are you feeling?</label>
          <textarea
            id="message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Describe your mood..."
            required
          ></textarea>
        </div>
        <div>
          <label htmlFor="date">Date</label>
          <input
            type="date"
            id="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? "Submitting..." : "Submit"}
        </button>
      </form>
      {success && <p style={{ color: "green" }}>{success}</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      <MoodLisT />
    </div>
  );
};

export default MoodForm;
