import React, { useState, useEffect } from "react";
import axios from "axios";
import MoodForm from "./MoodForm";
import MoodList from "./MoodList";

const Dashboard = () => {
  const [moods, setMoods] = useState([]);

  // Fetch moods from the backend

    const fetchMoods = async () => {
      try {
        const response = await axios.get("http://localhost:5000");
        setMoods(response.data);
      } catch (error) {
        console.error("Error fetching moods:", error);
      }
    };

    fetchMoods();


  // Add a new mood
  const addMood = async (mood) => {
    try {
      const response = await axios.post("http://localhost:5000", mood);
      setMoods([...moods, response.data]);
    } catch (error) {
      console.error("Error adding mood:", error);
    }
  };

  // Delete a mood
  const deleteMood = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/${id}`);
      setMoods(moods.filter((mood) => mood._id !== id));
    } catch (error) {
      console.error("Error deleting mood:", error);
    }
  };

  return (
    <div>
      <h1>Dashboard</h1>
      <MoodForm onAddMood={addMood} />
      <MoodList moods={moods} onDeleteMood={deleteMood} />
    </div>
  );
};

export default Dashboard;
