import React, { useState, useEffect } from "react";
import axios from "axios";
import MoodForm from "./MoodForm";
import { Link } from "react-router-dom";

const Dashboard = () => {
    const [latestMood, setLatestMood] = useState(null);

    // Fetch the latest mood from the backend
    const fetchLatestMood = async () => {
        try {
            const response = await axios.get("http://localhost:5000");
            if (response.data.length > 0) {
                setLatestMood(response.data[0]); // Assuming moods are sorted by creation date (latest first)
            }
        } catch (error) {
            console.error("Error fetching latest mood:", error);
        }
    };

    useEffect(() => {
        fetchLatestMood();
    }, []);

    // Add a new mood
    const addMood = async (mood) => {
        try {
            const response = await axios.post("http://localhost:5000", mood);
            setLatestMood(response.data); // Update the latest mood
        } catch (error) {
            console.error("Error adding mood:", error);
        }
    };

    return (
        <div>
            <h1>Dashboard</h1>
            <MoodForm onAddMood={addMood} />
            {latestMood ? (
                <ul>
                <li key={latestMood._id}>
                    <h3>Mood: {latestMood.mood}</h3>
                    <p>Message: {latestMood.message}</p>
                    <p>Date: {new Date(latestMood.date).toLocaleString()}</p>
                    <h4>Suggestions:</h4>
                    <ul>
                        {latestMood.suggestions.map((suggestion, index) => (
                            <li key={index}>{suggestion}</li>
                        ))}
                    </ul>
                    <button onClick={() => deleteMood(latestMood._id)}>Delete</button>
                </li>
            </ul>
            ) : (
                <p>No moods added yet.</p>
            )}
            <Link to="/history">
                <button>View Mood History</button>
            </Link>
        </div>
    );
};

export default Dashboard;
